document.querySelectorAll("header nav a").forEach((e=>{e.addEventListener("mouseover",(n=>{window.innerWidth<630||(e.style.animation="MakeHeaderNavShadow2 0.15s linear",e.style.boxShadow="0 2px 5px #1b1b40",e.style.width="88px",e.style.marginRight="7px")})),e.addEventListener("mouseout",(n=>{window.innerWidth<630||(e.style.animation="RemoveHeaderNavShadow2 0.15s linear",e.style.boxShadow="none",e.style.width="90px",e.style.marginRight="5px")}))}));let p=document.querySelector("aside nav > div");p.addEventListener("mouseover",(e=>{p.style.animation="DocsNavCloseBTN-Up 0.15s linear",p.style.boxShadow="0 2px 5px #1b1b40"})),p.addEventListener("mouseout",(e=>{p.style.animation="DocsNavCloseBTN-Down 0.15s linear",p.style.boxShadow="none"}));let open_nav_btn=document.querySelector("body > .open_nav_btn");open_nav_btn.addEventListener("click",(()=>{open_nav_btn.style.display="none",document.querySelector("body main aside").style.display="flex"}));let aside_cls_btn=document.querySelector("body main aside .cls");aside_cls_btn.addEventListener("click",(()=>{open_nav_btn.style.display="flex",document.querySelector("body main aside").style.display="none"}));